bg = Color.new(31, 31, 31)keyborder = Color.new(0, 0, 0)keybg = Color.new(16, 16, 16)keytext = Color.new(31, 31, 31)highlight = Color.new(23, 23, 23)bright = Color.new(21, 21, 29)keys = {{"1", "2", "3", "4", "5", "6", "7", "8", "9", "0", "-"},{"q", "w", "e", "r", "t", "y", "u", "i", "o", "p"},    {"a", "s", "d", "f", "g", "h", "j", "k", "l", "\n"},        {"z", "x", "c", "v", "b", "n", "m", ",", ".", "/"}}function getkbdinput(prompt, stringout)prompt = prompt or "Enter text: "stringout = stringout or ""while not Keys.held.A do  Controls.read()  screen.drawFillRect(SCREEN_UP, 0, 0, 256, 192, bg)  screen.drawFillRect(SCREEN_DOWN, 0, 0, 256, 192, bg)  screen.print(SCREEN_UP, 2, 2, prompt..stringout, keyborder)  for y, row in ipairs(keys) do   sy = y * 24   for x, key in ipairs(row) do      sx = x * 22 + y * 12 - 28      screen.drawGradientRect(SCREEN_DOWN, sx, sy, sx + 22, sy + 24, highlight, keybg, keybg, keybg)      screen.drawRect(SCREEN_DOWN, sx, sy, sx + 23, sy + 25, keyborder)      screen.print(SCREEN_DOWN, sx + 5, sy + 5, key, keytext)    end  end  if hx then    sy = hy * 24    sx = hx * 22 + hy * 12 - 28    screen.drawGradientRect(SCREEN_DOWN, sx, sy, sx + 22, sy + 24, bright, highlight, highlight, highlight)    screen.print(SCREEN_DOWN, sx + 5, sy + 5, keys[hy][hx], keytext)    screen.drawRect(SCREEN_DOWN, sx, sy, sx + 23, sy + 25, keyborder)  end  render()  if Keys.newPress.B then    stringout = string.sub(stringout, 1, -2)  end  if Stylus.held then    hy = math.floor(Stylus.Y / 24)    if keys[hy] then      hx = math.floor((Stylus.X + 28 - hy * 12)/ 22)      if not keys[hy][hx] then hx = nil end    else      hx = nil    end  elseif Stylus.released then    if hx then      stringout = stringout..keys[hy][hx]      hx = nil    end  endendreturn stringoutend